document.addEventListener('DOMContentLoaded', function () {
    const audio = document.getElementById('cursor-sound'); // Get the audio element
    const whooshAudio = document.getElementById('whoosh-sound');
    const customCursor = document.getElementById('custom-cursor'); // Get the custom cursor element
    const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent) || window.innerWidth <= 800;
    let sound;

    // Initialize balls with different speeds
    const balls = [
        { element: document.getElementById('ball1'), speedX: 0.5, speedY: 0.5, velocity: 0.5, clickCount: 0 }, // Slow
        { element: document.getElementById('ball2'), speedX: 1, speedY: 1, velocity: 1, clickCount: 0 }, // Medium
        { element: document.getElementById('ball4'), speedX: 1.5, speedY: 1.5, velocity: 1.5, clickCount: 0 } , // Fast
        { element: document.getElementById('ball3'), speedX: 2, speedY: 2, velocity: 2, clickCount: 0 }  // Fast
    ];

    function startGame() {
        balls.forEach(ball => {
            

            // Set initial random position ensuring balls don't go out of viewport
            ball.element.style.left = Math.random() * (window.innerWidth - ball.element.offsetWidth) + 'px';
            ball.element.style.top = Math.random() * (window.innerHeight - ball.element.offsetHeight) + 'px';

            // Add click event listener to each ball
            ball.element.addEventListener('click', (event) => {
                if (ball.element.id == 'ball1') {
                    console.log("In ball 1")
                    sound = document.getElementById('mamata'); // Get the audio element
                } else if (ball.element.id == 'ball2') {
                    console.log("In ball 2")
    
                    // message += `Tamacha count for Modi: ${ball.clickCount} times\n`;
                    sound = document.getElementById('modi'); // Get the audio element
                } else if (ball.element.id == 'ball3') {
                    console.log("In ball 3")
    
                    // message += `Tamacha count for Rahul: ${ball.clickCount} times\n`;
                    sound = document.getElementById('rahul'); // Get the audio element
                }
                else if (ball.element.id == 'ball4') {
                    console.log("In ball 4")
    
                    // message += `Tamacha count for Rahul: ${ball.clickCount} times\n`;
                    sound = document.getElementById('sitaraman'); // Get the audio element
                }
                if (sound) {  // Ensure the sound variable is not null or undefined
                    sound.currentTime = 0; // Reset audio to start
                    sound.play();           // Play the audio
                }


                ball.clickCount += 1;
                console.log(`Ball ${ball.element.id} clicked ${ball.clickCount} times`);

                handleCursorChange(event, false)

                event.stopPropagation(); // Prevents the click from bubbling to the body
                audio.currentTime = 0; // Reset audio to start
                audio.play();
                ball.element.classList.add('custom-cursor-click'); // Add custom cursor class

               
                setTimeout(() => {
                    ball.element.classList.remove('custom-cursor-click'); // Remove custom cursor class after 1 second
                }, 1000);
            });
        });
    }

    // Function to handle cursor change, play audio, and revert after 1 second
    function handleCursorChange(event, body) {
        const pic1 = "data:image/png;base64,"
        const pic2 = "data:image/png;base64,"

        const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent) || window.innerWidth <= 800;
        if (!body) {

            audio.currentTime = 0; // Reset audio to start
            audio.play(); // Play the audio
        } else {
            whooshAudio.currentTime = 0;
            whooshAudio.play()
        }
        document.body.classList.add('custom-cursor-click'); // Add custom cursor class to body

        customCursor.style.display = 'block';
        customCursor.style.backgroundImage = `url('${pic1}')`;
        customCursor.style.backgroundSize = "cover";

        customCursor.style.backgroundPosition = "center";
        customCursor.style.backgroundRepeat = "no-repeat";

        customCursor.style.left = `${event.clientX - 15}px`; // Center the cursor
        customCursor.style.top = `${event.clientY - 15}px`;  // Center the cursor


        setTimeout(() => {
            document.body.classList.remove('custom-cursor-click'); // Remove custom cursor class from body after 1 second

            customCursor.style.backgroundImage = `url('${pic2}')`;
            setTimeout(() => {

                customCursor.style.display = 'none'; // Hide the custom cursor
            }, 100)



        }, 100);
    }

    // Function to move the balls
    function moveBalls() {
        balls.forEach(ball => {
            let rect = ball.element.getBoundingClientRect();

            // Update position
            let newX = rect.left + ball.speedX * ball.velocity;
            let newY = rect.top + ball.speedY * ball.velocity;

            // Check boundaries and reverse direction if needed
            if (newX <= 0 || newX >= window.innerWidth - ball.element.offsetWidth) ball.speedX *= -1;
            if (newY <= 0 || newY >= window.innerHeight - ball.element.offsetHeight) ball.speedY *= -1;

            ball.element.style.left = (rect.left + ball.speedX * ball.velocity) + 'px';
            ball.element.style.top = (rect.top + ball.speedY * ball.velocity) + 'px';
        });

        requestAnimationFrame(moveBalls);
    }

    moveBalls();
    document.addEventListener('mousemove', updateCursorPosition);
    document.addEventListener('touchmove', updateCursorPosition);

    function updateCursorPosition(event) {
        const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent) || window.innerWidth <= 800;

        let clientX, clientY;

        if (event.touches) {
            clientX = event.touches[0].clientX;
            clientY = event.touches[0].clientY;
        } else {
            clientX = event.clientX;
            clientY = event.clientY;
        }

        
        customCursor.style.left = `${clientX - 150}px`; // Adjust for cursor size
        customCursor.style.top = `${clientY - 200}px`;

    }


    // Add click event listener to the body for handling clicks outside the balls
    document.body.addEventListener('click', (event) => {
        if (!event.target.classList.contains('ball')) {
            handleCursorChange(event, true);
        }
    });

    // Set a timer for 30 seconds (30000 milliseconds)
    setTimeout(function () {
        const modal = document.getElementById('clickCountModal');
        const clickCounts = document.getElementById('click-counts');
        let message = '';

        balls.forEach(ball => {
            if (ball.element.id == 'ball1') {
                message += `Tamacha count for Mamata: ${ball.clickCount} times</p>`
            }
            else if (ball.element.id == 'ball2') {
                message += `Tamacha count for Modi: ${ball.clickCount} times</p>`
            }
            else if (ball.element.id == 'ball3') {
                message += `Tamacha count for Rahul: ${ball.clickCount} times</p>`
            }
            else if (ball.element.id == 'ball4') {
                message += `Tamacha count for Sitaraman: ${ball.clickCount} times</p>`
            }
            // console.log("ID",ball.element.id);

            // message += `<p>Ball ${ball.element.id} has been clicked ${ball.clickCount} times</p>`;
        });

        clickCounts.innerHTML = message;
        modal.style.display = 'block';

        // Close the modal when the user clicks on the close button
        document.querySelector('.close').onclick = function () {
            modal.style.display = 'none';
            window.location.reload()
        };

        // Close the modal when the user clicks anywhere outside of the modal
        
    }, 35000);
    const countdownElement = document.getElementById('countdown');

    let countdown = 3;
    function updateCountdown() {
        if (countdown > 0) {
            countdownElement.innerHTML = ` <br>
             Play 30 secs for result `;
            countdown--;
            setTimeout(updateCountdown, 1000); // Update countdown every second
        } else {
            countdownElement.style.display = 'none'; // Hide countdown
            startGame(); // Call the function to start the game
        }
    }
    updateCountdown()
});


